package zhiyuan.com.flowers.activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.RelativeLayout;import zhiyuan.com.flowers.R;public class SuccessRateActivity extends BaseActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_success_rate);        initView();    }    private void initView() {        RelativeLayout rl_sucess_first = (RelativeLayout) findViewById(R.id.rl_sucess_first);        MyOnclickListener myOnclickListener = new MyOnclickListener();        rl_sucess_first.setOnClickListener(myOnclickListener);    }    class MyOnclickListener implements View.OnClickListener{        @Override        public void onClick(View view) {            switch (view.getId()){                case R.id.rl_sucess_first:                    startActivity(new Intent(SuccessRateActivity.this, CreditDetailActivity.class));                    break;            }        }    }    public void back(View view){        finish();    }}